$.fn.parallaxify=function(){return this.each(function(){function n(){var n=$(window).height(),i=$(window).scrollTop(),r=o.offset().top,t=o.height();if(r>i+n)return 0;if(i>r+t)return 100;var c=i+n-r,s=c/((n+t)/100);return s}var o=$(this);o.css({"background-position-y":n()+"%"}),$(window).on("scroll",function(){o.css({"background-position-y":n()+"%"})})})};